import React, { useRef } from 'react';
import customer from '../images/customer.png';
import { useState,useEffect } from 'react';
import { useHistory } from "react-router-dom";
import { environmentConfig } from '../environment.config';

const CustomerLogin = ()=>{

  const userRef = useRef();
  const errRef = useRef();
  const history = useHistory();
  const [user, setUser] = useState('');
  const [pwd, setPwd] = useState('');
  const [errMsg, setErrMsg] = useState('');

  useEffect(() => {
    userRef.current.focus();
  },[])

  useEffect(() => {
    setErrMsg('');
  },[user,pwd])  

  const handleSubmit = async (e) => {
    e.preventDefault();
    console.log(user,pwd); 
    //history.push("/customer?username="+user);
    const userName = user;
    try {
      //const data = await client.query(`SELECT * FROM emp_login WHERE username = $1;`, [user]) //Verifying if the user exists in the database

      const query = await fetch(`${environmentConfig.apiEndPoint}/customer/${userName}`);
      const data = await query.json();
      console.log(typeof(data.username));

      if(data.username == userName)
      {
        console.log("login success"); 
      }
      //console.log(data);
      /*if (user.length === 0) {
      console.log("login success");
    } */
  }catch(e){console.log("an error occur"+e);} 

  }


    return (
<div>
    <br></br>
    <section>
      <p ref={errRef} className={errMsg ? "errmsg" : "offscreen"} aria-live='assertive'>{errMsg}</p>
    </section>
<h1 className="mt-5 mb-5" style={{textAlign : "center"}}>Customer Login</h1>
<div className="border container p-0 shadow-lg p-3 mb-5 bg-white rounded "  style={{display: 'flex',  justifyContent:'center'}}>
    <img src={customer} alt="customer missing" className="rounded mx-auto d-block ml-10 mr-10 flex-left p-5" height="400px"></img>
    <form className='container flex-right p-5 ' style={{alignSelf : "right"}} onSubmit={handleSubmit} >

  <div className="form-group">
    <label className="mt-4">Username</label>
    <input 
      type="text" 
      className="form-control mb-3" 
      id="exampleInputEmail1" 
      ref={userRef} 
      autoComplete='off' 
      onChange={(e) => setUser(e.target.value)}
      value={user}
      name="username" 
      aria-describedby="emailHelp"  
      required
    />
    <label >Password</label>
    <input 
      type="password" 
      className="form-control" 
      id="exampleInputPassword1"
      onChange={(e) => setPwd(e.target.value)}
      value={pwd}   
      required

    />
  </div>

  <button type="submit" className="btn btn-primary btn-lg mt-3">Login</button>
</form>
</div>
</div>
    );
};

export default CustomerLogin;
